import React, { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";
import { FaTrash, FaEdit, FaPlus, FaCarAlt } from "react-icons/fa";

// Styled Components
const PageWrapper = styled.div`
  display: flex;
  height: 100vh;
  background-color: #f4f4f9;
`;

const Sidebar = styled.div`
  width: 250px;
  background-color: #333;
  color: #fff;
  padding: 20px;
  display: flex;
  flex-direction: column;
`;

const SidebarItem = styled.div`
  padding: 15px 10px;
  margin-bottom: 10px;
  border-radius: 5px;
  cursor: pointer;
  color: ${(props) => (props.active ? "#007bff" : "#fff")};
  background-color: ${(props) => (props.active ? "#444" : "transparent")};
  display: flex;
  align-items: center;
  gap: 10px;

  &:hover {
    background-color: #444;
  }
`;

const GarageContainer = styled.div`
  flex: 1;
  padding: 20px;
  color: #333;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
`;

const Button = styled.button`
  background-color: #007bff;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background-color: #0056b3;
  }
`;

const CarsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 20px;
`;

const CarCard = styled.div`
  background: #fff;
  border-radius: 8px;
  padding: 20px;
  position: relative;
  text-align: center;
  box-shadow: 0px 4px 12px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s;

  &:hover {
    transform: translateY(-5px);
  }

  &:hover div {
    display: flex;
  }
`;

const CarImage = styled.img`
  width: 100%;
  height: 120px;
  object-fit: cover;
  border-radius: 4px;
  margin-bottom: 10px;
`;

const DefaultIcon = styled(FaCarAlt)`
  font-size: 80px;
  color: #ccc;
  margin-bottom: 10px;
`;

const CarDetails = styled.div`
  text-align: left;
`;

const Modal = styled.div`
  background: #fff;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0px 4px 12px rgba(0, 0, 0, 0.2);
  color: #333;
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 400px;
  z-index: 10;
`;

const Overlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.6);
  z-index: 5;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

function Garage() {
  const [cars, setCars] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [formData, setFormData] = useState({ make: "", model: "", category: "", image: null });
  const [editCarId, setEditCarId] = useState(null);

  const fetchCars = async () => {
    const response = await axios.get("http://localhost:5000/cars");
    setCars(response.data);
  };

  useEffect(() => {
    fetchCars();
  }, []);

  const handleAddCar = async (e) => {
    e.preventDefault();

    const formDataToSend = new FormData();
    formDataToSend.append("make", formData.make);
    formDataToSend.append("model", formData.model);
    formDataToSend.append("category", formData.category);
    if (formData.image) {
      formDataToSend.append("image", formData.image);
    }

    if (editCarId) {
      await axios.put(`http://localhost:5000/cars/${editCarId}`, formDataToSend);
    } else {
      await axios.post("http://localhost:5000/cars", formDataToSend);
    }

    setFormData({ make: "", model: "", category: "", image: null });
    setEditCarId(null);
    setIsModalOpen(false);
    fetchCars();
  };

  const handleDeleteCar = async (id) => {
    await axios.delete(`http://localhost:5000/cars/${id}`);
    fetchCars();
  };

  const handleEditCar = (car) => {
    setFormData(car);
    setEditCarId(car._id);
    setIsModalOpen(true);
  };

  return (
    <PageWrapper>
      <Sidebar>
        <SidebarItem active>
          <FaCarAlt /> Garage
        </SidebarItem>
        <SidebarItem>
          <FaPlus /> Dashboard
        </SidebarItem>
        <SidebarItem>
          <FaEdit /> Tracks
        </SidebarItem>
        <SidebarItem>
          <FaTrash /> Events
        </SidebarItem>
        <SidebarItem>Settings</SidebarItem>
      </Sidebar>
      <GarageContainer>
        <Header>
          <h2>Garage</h2>
          <Button onClick={() => setIsModalOpen(true)}>
            <FaPlus /> Add Car
          </Button>
        </Header>

        <CarsGrid>
          {cars.map((car) => (
            <CarCard key={car._id}>
              {car.image ? <CarImage src={car.image} alt={car.make} /> : <DefaultIcon />}
              <CarDetails>
                <p><strong>Make:</strong> {car.make}</p>
                <p><strong>Model:</strong> {car.model}</p>
                <p><strong>Category:</strong> {car.category}</p>
              </CarDetails>
            </CarCard>
          ))}
        </CarsGrid>

        {isModalOpen && (
          <>
            <Overlay onClick={() => setIsModalOpen(false)} />
            <Modal>
              <h3>{editCarId ? "Edit Car" : "Add Car"}</h3>
              <form onSubmit={handleAddCar}>
                <Input
                  type="text"
                  placeholder="Make"
                  value={formData.make}
                  onChange={(e) => setFormData({ ...formData, make: e.target.value })}
                  required
                />
                <Input
                  type="text"
                  placeholder="Model"
                  value={formData.model}
                  onChange={(e) => setFormData({ ...formData, model: e.target.value })}
                  required
                />
                <Input
                  type="text"
                  placeholder="Category"
                  value={formData.category}
                  onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                  required
                />
                <Input
                  type="file"
                  onChange={(e) => setFormData({ ...formData, image: e.target.files[0] })}
                />
                <Button type="submit">{editCarId ? "Save Changes" : "Add Car"}</Button>
              </form>
              <Button onClick={() => setIsModalOpen(false)}>Close</Button>
            </Modal>
          </>
        )}
      </GarageContainer>
    </PageWrapper>
  );
}

export default Garage;
