import React, { useState, useEffect } from "react";
import Sidebar from "../components/Sidebar";
import styled from "styled-components";

const images = [
    "/images/image1.jpg",
    "/images/image2.jpg",
    "/images/image3.jpg",
    "/images/image4.jpg",
    "/images/image5.jpg",
    "/images/image6.jpg",
    "/images/image7.jpg",
    "/images/image8.jpg",
    "/images/image9.jpg",
    "/images/image10.jpg",
    "/images/image11.jpg",
    "/images/image12.jpg",
  ];
  

const DashboardContainer = styled.div`
  display: flex;
  height: 100vh;
  background: url(${(props) => props.background}) center/cover no-repeat;
  transition: background 0.5s ease;
`;

const Content = styled.div`
  margin-left: ${(props) => (props.sidebarOpen ? "200px" : "60px")};
  padding: 20px;
  width: 100%;
  transition: margin-left 0.3s ease;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: rgba(255, 255, 255, 0.8);
  padding: 15px;
  border-radius: 8px;
  margin-bottom: 20px;
`;

const Section = styled.div`
  display: flex;
  justify-content: space-between;
  gap: 20px;
`;

const Card = styled.div`
  flex: 1;
  background: rgba(255, 255, 255, 0.9);
  border-radius: 8px;
  padding: 20px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
`;

function Dashboard() {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [background, setBackground] = useState("");

  useEffect(() => {
    const randomImage = images[Math.floor(Math.random() * images.length)];
    setBackground(randomImage);
  }, []);

  return (
    <DashboardContainer background={background}>
      <Sidebar isOpen={sidebarOpen} toggleSidebar={() => setSidebarOpen(!sidebarOpen)} />
      <Content sidebarOpen={sidebarOpen}>
        <Header>
          <h2>Dashboard</h2>
          <input type="search" placeholder="Search something..." />
        </Header>
        <Section>
          <Card>
            <h3>Upcoming Race</h3>
            <p>Name: Nurburgring Endurance</p>
            <p>Date: Dec 15, 2024</p>
            <p>Car: Porsche 911 GT3</p>
          </Card>
          <Card>
            <h3>Your Performance</h3>
            <p>Last Race: 2nd Place</p>
            <p>Total Races: 23</p>
            <p>Tracks Raced On: 10</p>
          </Card>
        </Section>
      </Content>
    </DashboardContainer>
  );
}

export default Dashboard;
