import React, { useState } from "react";
import styled from "styled-components";
import { FaBars, FaCar, FaCalendarAlt, FaUser, FaCogs, FaRoad } from "react-icons/fa";

const SidebarContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  height: 100%;
  width: ${(props) => (props.isOpen ? "200px" : "60px")};
  background-color: #2c3e50;
  color: white;
  transition: width 0.3s ease;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const MenuToggle = styled.div`
  margin: 10px;
  cursor: pointer;
  font-size: 24px;
`;

const MenuItem = styled.div`
  display: flex;
  align-items: center;
  justify-content: ${(props) => (props.isOpen ? "flex-start" : "center")};
  padding: 10px 20px;
  width: 100%;
  cursor: pointer;
  color: ${(props) => (props.active ? "#1abc9c" : "white")};
  font-size: ${(props) => (props.isOpen ? "16px" : "20px")};
  transition: all 0.3s ease;

  &:hover {
    background-color: #34495e;
  }

  span {
    margin-left: ${(props) => (props.isOpen ? "10px" : "0")};
    display: ${(props) => (props.isOpen ? "inline" : "none")};
  }
`;

function Sidebar({ isOpen, toggleSidebar }) {
  const [active, setActive] = useState("Dashboard");

  const menuItems = [
    { name: "Dashboard", icon: <FaBars /> },
    { name: "Profile", icon: <FaUser /> },
    { name: "Garage", icon: <FaCar /> },
    { name: "Tracks", icon: <FaRoad /> },
    { name: "Events", icon: <FaCalendarAlt /> },
    { name: "Settings", icon: <FaCogs /> },
  ];

  return (
    <SidebarContainer isOpen={isOpen}>
      <MenuToggle onClick={toggleSidebar}>{isOpen ? <FaBars /> : <FaBars />}</MenuToggle>
      {menuItems.map((item) => (
        <MenuItem
          key={item.name}
          isOpen={isOpen}
          active={active === item.name}
          onClick={() => setActive(item.name)}
        >
          {item.icon}
          <span>{item.name}</span>
        </MenuItem>
      ))}
    </SidebarContainer>
  );
}

export default Sidebar;
