import React, { useState, useEffect } from "react";
import axios from "axios";
import styled from "styled-components";

// Array of images for random background
const images = [
  "/images/image1.jpg",
  "/images/image2.jpg",
  "/images/image3.jpg",
  "/images/image4.jpg",
  "/images/image5.jpg",
  "/images/image6.jpg",
  "/images/image7.jpg",
  "/images/image8.jpg",
  "/images/image9.jpg",
  "/images/image10.jpg",
  "/images/image11.jpg",
  "/images/image12.jpg",
];


// Styled components
const Wrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background: url(${(props) => props.background}) center/cover no-repeat;
  transition: background 0.5s ease;
`;

const FormContainer = styled.div`
  background: rgba(0, 0, 0, 0.7);
  padding: 30px;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
  color: white;
  width: 350px;
`;

const Title = styled.h2`
  text-align: center;
  margin-bottom: 20px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin: 10px 0;
  border: none;
  border-radius: 4px;
`;

const Button = styled.button`
  width: 100%;
  padding: 10px;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #0056b3;
  }
`;

const ErrorMessage = styled.p`
  color: red;
  font-size: 14px;
  margin-bottom: 10px;
  text-align: center;
`;

const ToggleLink = styled.p`
  text-align: center;
  margin-top: 20px;
  color: #007bff;
  cursor: pointer;

  &:hover {
    text-decoration: underline;
  }
`;

function Login() {
  const [isLogin, setIsLogin] = useState(true); // Toggle between login and create account
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [background, setBackground] = useState("");

  useEffect(() => {
    // Set a random background image on load
    const randomImage = images[Math.floor(Math.random() * images.length)];
    setBackground(randomImage);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    const endpoint = isLogin ? "/auth/login" : "/auth/register";

    try {
      const response = await axios.post(`http://localhost:5000${endpoint}`, {
        username,
        password,
      });

      if (isLogin) {
        // Login success
        alert(`Welcome, ${response.data.username}`);
        window.location.href = "/dashboard"; // Redirect to dashboard
      } else {
        // Registration success
        alert("Account created successfully! Please login.");
        setIsLogin(true); // Switch back to login mode
      }
    } catch (err) {
      setError(err.response?.data?.message || "Something went wrong");
    }
  };

  return (
    <Wrapper background={background}>
      <FormContainer>
        <Title>{isLogin ? "Login" : "Create Account"}</Title>
        {error && <ErrorMessage>{error}</ErrorMessage>}
        <form onSubmit={handleSubmit}>
          <Input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <Input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <Button type="submit">{isLogin ? "Login" : "Create Account"}</Button>
        </form>
        <ToggleLink onClick={() => setIsLogin(!isLogin)}>
          {isLogin ? "Create an account" : "Back to login"}
        </ToggleLink>
      </FormContainer>
    </Wrapper>
  );
}

export default Login;
